import 'package:equatable/equatable.dart';class NotificationData extends Equatable {  final int id;  final String img;  final String title;  final String subTitle;  const NotificationData({    required this.id,    required this.img,    required this.title,    required this.subTitle,  });  @override  List<Object?> get props => [id, img, title, subTitle];  @override  bool get stringify => true;  // You can override other methods like hashCode, ==, etc. as needed  @override  String toString() {    return 'NotificationData(id: $id, img: $img, title: $title, subTitle: $subTitle)';  }}